/*!
 \ @brief nodejs base module
 */
var $ =  module.exports = (function() {
	var $;
	$ = {
		'trace': function(s) {console.log(s);},
		'timestamp': function () { //时间戳 
			return Date.now();
		},
		'ext': function(target, src) {
			for (var i in src) {
				if (src.hasOwnProperty(i)) {
					target[i] = src[i];
				}
			}
			return target;
		},
		'rnd' : function(a, b) {return Math.round(Math.random()*(b-a))+a}
	};
	var S; // 针对string操作,包括正则,介入String.prototype
	S = {
		toLow:function() {return this.toLowerCase()},
		toUp:function() {return this.toUpperCase()},
		replaceAll:function(s1,s2) {var a=this.split(s1); return a.join(s2)},
		trim:function(){return this.replace(/\s+/g, '')}
	};
	$.ext(String.prototype, S);
	return $;
})();



